mapper.select:
select *
from video
where course_id = #{course_id}
  and session = #{session};

select count(*) as cnt
from video
where course_id = #{course_id};

select *
from video
where course_id = #{course_id}
  and session = #{session};

mapper.update:
update video
set score = #{score}
where course_id = #{course_id}
  and session = #{session};

mapper.select:
select video_score
from stu_session_score
where sess_id = #{id}
  and stu_id = #{user};

select test_score
from stu_session_score
where sess_id = #{id}
  and stu_id = #{user};

mapper.update:
update stu_session_score
set video_score = #{score}
where stu_id = #{user}
  and sess_id = #{sess_id};

update stu_session_score
set test_score = #{score}
where stu_id = #{user}
  and sess_id = #{sess_id};

update session_test
set question_type=#{questionType},
    title=#{title},
    choices=#{choices},
    correct_answer = #{correct_answer}
where id = #{id};
